import React, { useEffect, useState } from 'react';
import axios from '../api/axios';

function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    axios.get('/tasks/user/1')
      .then(res => setTasks(res.data))
      .catch(console.error);
  }, []);

  const handleCreateTask = async () => {
    try {
      const payload = { title, description, userId: 1, tagIds: [1] }; // static user/tag for demo
      await axios.post('/tasks', payload);
      alert('Task created!');
      window.location.reload();
    } catch (err) {
      alert('Error creating task');
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-xl mb-4">Dashboard</h1>
      <div className="mb-4">
        <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} className="border p-2 mr-2" />
        <input type="text" placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} className="border p-2 mr-2" />
        <button onClick={handleCreateTask} className="bg-green-500 text-white px-4 py-2">Add Task</button>
      </div>
      <ul>
        {tasks.map(task => (
          <li key={task.id} className="mb-2">{task.title}: {task.description}</li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
